cd /home/huy/Project/BTL && sudo mysql -u root -e "USE web; SELECT DISTINCT ncc.MaNCC, ncc.TenNCC, COUNT(DISTINCT ct.MaSP) as SoSanPham FROM nhacungcap ncc INNER JOIN phieunhap pn ON ncc.MaNCC = pn.MaNCC INNER JOIN chitietphieunhap ct ON pn.MaPN = ct.MaPN GROUP BY ncc.MaNCC, ncc.TenNCC ORDER BY SoSanPham DESC LIMIT 10;" || echo "Cannot connect to database. Checking SQL file instead..."